function internationalize(e){return e.replace(/__MSG_(\w+)__/g,function(e,n){return"undefined"!==chrome.i18n.getMessage(n)&&""!==chrome.i18n.getMessage(n)?chrome.i18n.getMessage(n):"[Error:"+n+"]"})}function setLocalization(e){var n=document.getElementsByTagName(e||"body")[0];n.innerHTML=internationalize(n.innerHTML)}document.addEventListener("DOMContentLoaded",function(){setLocalization()});