function i18n(n){return chrome.i18n.getMessage(n)}function internationalize(n){return n.replace(/__MSG_(\w+)__/g,function(n,e){return"undefined"!==i18n(e)&&""!==i18n(e)?i18n(e):"[Error:"+e+"]"})}function setLocalization(n){var e=document.getElementsByTagName(n||"body")[0];e.innerHTML=internationalize(e.innerHTML)}document.addEventListener("DOMContentLoaded",function(){setLocalization()});